import React, { useState, useEffect } from 'react';
import CryptoJS from 'crypto-js';

const SECRET = 'YangsaL2025SecretKey'; // 与生成脚本一致
const TRIAL_DAYS = 30;

function verifyCode(inputCode) {
  try {
    const decoded = atob(inputCode.replace(/-/g, '+').replace(/_/g, '/'));
    const parts = decoded.split('|');

    if (parts.length !== 4) {
      return { valid: false, reason: 'format' };
    }

    const [activationMarker, licenseType, days, sign] = parts;

    // 检查是否是新的通用激活码格式
    if (activationMarker === 'ACTIVATE_DATE') {
      const data = `${activationMarker}|${licenseType}|${days}`;
      const expectedSign = CryptoJS.HmacSHA256(data, SECRET).toString().slice(0, 16);

      if (sign === expectedSign) {
        // 计算激活日期（今天）和到期日期
        const activationDate = new Date().toISOString().split('T')[0];
        const activationDt = new Date(activationDate);
        const expireDt = new Date(activationDt.getTime() + parseInt(days) * 24 * 60 * 60 * 1000);
        const expireDate = expireDt.toISOString().split('T')[0];

        return { 
          valid: true, 
          expireDate,
          activationDate,
          licenseType,
          days: parseInt(days)
        };
      } else {
        return { valid: false, reason: 'invalid' };
      }
    } else {
      // 兼容旧的激活码格式
      const [activationDate, expireDate, oldLicenseType, oldSign] = parts;
      const data = `${activationDate}|${expireDate}|${oldLicenseType}`;
      const expectedSign = CryptoJS.HmacSHA256(data, SECRET).toString().slice(0, 16);

      if (oldSign === expectedSign) {
        const now = new Date();
        const exp = new Date(expireDate);
        if (now <= exp) {
          return { 
            valid: true, 
            expireDate,
            activationDate,
            licenseType: oldLicenseType,
            days: Math.ceil((exp - new Date(activationDate)) / (1000 * 60 * 60 * 24))
          };
        } else {
          return { valid: false, reason: 'expired', expireDate };
        }
      } else {
        return { valid: false, reason: 'invalid' };
      }
    }
  } catch {
    return { valid: false, reason: 'format' };
  }
}

function checkCodeUsage(code) {
  // 检查激活码是否已被使用
  const usedCodes = JSON.parse(localStorage.getItem('usedCodes') || '[]');
  return usedCodes.includes(code);
}

function markCodeAsUsed(code) {
  // 标记激活码为已使用
  const usedCodes = JSON.parse(localStorage.getItem('usedCodes') || '[]');
  if (!usedCodes.includes(code)) {
    usedCodes.push(code);
    localStorage.setItem('usedCodes', JSON.stringify(usedCodes));
  }
}

function getTrialInfo() {
  let trialStart = localStorage.getItem('trialStart');
  if (!trialStart) {
    trialStart = new Date().toISOString();
    localStorage.setItem('trialStart', trialStart);
  }
  const start = new Date(trialStart);
  const now = new Date();
  const diff = Math.floor((now - start) / (1000 * 60 * 60 * 24));
  const left = TRIAL_DAYS - diff;
  const expireDate = new Date(start.getTime() + TRIAL_DAYS * 24 * 60 * 60 * 1000);
  return { left, expireDate };
}

export default function LicenseModal({ visible, onActivated, onClose }) {
  const [input, setInput] = useState('');
  const [status, setStatus] = useState('');
  const [activated, setActivated] = useState(false);
  const [expireDate, setExpireDate] = useState(null);
  const [trial, setTrial] = useState(getTrialInfo());

  useEffect(() => {
    // 检查本地激活状态
    const lic = localStorage.getItem('license');
    if (lic) {
      const res = verifyCode(lic);
      if (res.valid) {
        setActivated(true);
        setExpireDate(res.expireDate);
        if (onActivated) onActivated(res.expireDate);
      } else {
        setActivated(false);
        setExpireDate(null);
      }
    }
  }, [visible]);

  const handleActivate = () => {
    const code = input.trim();

    // 检查激活码是否已被使用
    if (checkCodeUsage(code)) {
      setStatus('used');
      return;
    }

    const res = verifyCode(code);
    if (res.valid) {
      // 标记激活码为已使用
      markCodeAsUsed(code);

      // 保存激活信息
      localStorage.setItem('license', code);
      localStorage.setItem('activationDate', res.activationDate);
      localStorage.setItem('licenseType', res.licenseType);

      setActivated(true);
      setExpireDate(res.expireDate);
      setStatus('success');
      if (onActivated) onActivated(res.expireDate);
    } else if (res.reason === 'expired') {
      setStatus('expired');
      setExpireDate(res.expireDate);
    } else {
      setStatus('fail');
    }
  };

  // 试用到期判断
  const trialExpired = trial.left <= 0;

  if (!visible) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-30">
      <div className="bg-white rounded-2xl shadow-2xl p-8 w-[90vw] max-w-xs border border-gray-100 relative">
        <h2 className="text-lg font-semibold text-center mb-2 text-gray-700">软件激活</h2>
        {activated ? (
          <div className="text-green-600 text-center mb-4">
            已激活，有效期至 <b>{expireDate}</b>
          </div>
        ) : trialExpired ? (
          <div className="text-red-500 text-center mb-4">试用已到期，请输入激活码</div>
        ) : (
          <div className="text-gray-500 text-center mb-4">
            当前为试用版，剩余 <b>{trial.left}</b> 天<br />
            到期后需输入激活码继续使用
          </div>
        )}
        {!activated && (
          <>
            <input
              className="w-full border rounded px-3 py-2 mb-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="请输入激活码"
              value={input}
              onChange={e => setInput(e.target.value)}
              disabled={activated}
            />
            <button
              className="w-full bg-blue-500 text-white rounded py-2 font-medium hover:bg-blue-600 transition mb-2"
              onClick={handleActivate}
              disabled={activated}
            >激活</button>
            {status === 'success' && <div className="text-green-600 text-center text-xs">激活成功！</div>}
            {status === 'fail' && <div className="text-red-500 text-center text-xs">激活码无效</div>}
            {status === 'expired' && <div className="text-orange-500 text-center text-xs">激活码已过期（到期日：{expireDate}）</div>}
            {status === 'used' && <div className="text-red-500 text-center text-xs">激活码已被使用，无法重复激活</div>}
          </>
        )}
        {activated && (
          <button className="absolute top-2 right-2 text-gray-400 hover:text-blue-500" onClick={onClose}>
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        )}
      </div>
    </div>
  );
}
